import React, {Component} from 'react'
import ToyslandService from '../../services/ToyslandService'
import { WithAuthConsumer } from '../context/AuthContext'
import { Link } from 'react-router-dom'

class SingleProduct extends Component {

    state = {
        product: {},
        user: {}
    }

    componentDidMount() { 
        ToyslandService.getThisProduct(this.props.match.params.id)
            .then(product => {
                this.setState({product})
            })
        ToyslandService.getUser()
            .then(user => {
                this.setState({user: user.find(user => user.id === this.props.match.params.id)})
            })
    }

    render() {
        return(

    <div className="SingleProductContainer">
        <div className="SingleProductContainerContainer">
            <div className="SingleProductContainerContainerContainer">

                <div className="single-card-image">
                    <div className="header-container">
                        <div className="header-user-container">
                            <div className="user-pic">{this.state.product.user}</div>
                            <div className="user-username">{this.state.product.user}</div>
                        </div>
                        <div className="buttons-container">
                            <div className="button-lovit"><button>Love It</button></div>
                            <div className="button-message"><Link to="/">Ch</Link></div>
                        </div>
                    </div>
                    <div className="single-card-image-container">
                        <img src={this.state.product.productPicture} alt="" />
                    </div>
                    <div className="card-data-container">
                        <div className="card-price">{this.state.product.price}â‚¬</div>
                        <div className="card-name">{this.state.product.name}</div>
                        <div className="card-description">{this.state.product.description}</div>                                  
                    </div>
                </div>

            </div>
        </div>
    </div>
        )
    }



}

export default WithAuthConsumer (SingleProduct)
