import React, { Component } from 'react'
import { Redirect } from 'react-router-dom'
import ToyslandService from '../../services/ToyslandService'
import { WithAuthConsumer } from '../context/AuthContext'
import Lateral from '../misc/Lateral'
import ProductList from './ProductList'

class Products extends Component {

    state = {
        data: {
            product: this.props.currentUser.product
        },
        loading: true
    }

    componentDidMount() {

        ToyslandService.profile(this.props.currentUser.product)
            .then(currentUser => {
                if(!this.props.currentUser.product) {
                    return <Redirect to="/products" />
                }else{
                  this.props.setUser({...this.props.currentUser, product: [...this.props.currentUser.product, product]})  
                }
            })
    }

    render() {
        return(
            <div className="EditContainer">

                <div className="LateralDiv">
                    <Lateral />
                </div>

                    <div className="MyProducts">
                        {this.state.products.map((product, index) => (
                            <ProductList {...product} key= {index} />
                        ))}
                    </div>
            </div>
        )
    }

}

export default WithAuthConsumer (Products)
