import React, { Component } from 'react'
import ToyslandService from '../../services/ToyslandService'
import Lateral from '../misc/Lateral'
import { WithAuthConsumer } from '../context/AuthContext'


class Chat extends Component {

    constructor(props) {
        super(props)

        this.state = {
            chat: {}
        }
    }
        componentDidMount() {

            ToyslandService.showChat(this.props.match.params.id) // Porqué esto?
                .then(chat => {
                    this.setState({chat: chat}, () => console.log('params', this.state.chat))
            })
        }

        render() {  

            return(
                <div className="EditContainer">

                    <div className="LateralDiv">
                        <Lateral />
                    </div>

                     <div className='EditContainerContainerChat'>

                        <div className="blockOne">
                            <div className="bandeja">
                                Bandeja de Entrada
                            </div>

                            <div className="productToShow">
                                {this.state.chat.product && this.state.chat.product.name}
                            </div>
                        </div>

                        <div className="blockTwo">
                            <div className="bandeja">

                            </div>

                            <div className="SalerMessage">
                                {/* {this.state.chat.products.productPicture} */}
                            </div>

                            <div className="SendMessage">
                                <input type="text" class="chat-footer__form-input" placeholder="New message" />
                                <button type="submit">Enviar</button>
                            </div>
                        </div>

                        <div className="blockThree">
                            <div className="productCard">

                            </div>

                        </div>

                    </div>
                </div>
            )
        }



}

export default WithAuthConsumer  (Chat)
