import React, { Component } from 'react'
import ToyslandService from '../../services/ToyslandService'
import { Link, Redirect} from 'react-router-dom'

class Register extends Component {

        state = {
            data: {
                name: '',
                email: '',
                username: '',
                password: '',
                avatar: null
            },
            error: false,
            loading: false,
            success: false
        }

        handleChange = (event) => {
            const { name, value, files} = event.target

            this.setState({
                data: {
                    ...this.state.data,
                    [name] : files ? files[0] : value
                }
            })
        }

        handleSubmit = (event) => {
            event.preventDefault()


            const formData = new FormData()
                formData.append('name',this.state.name)
                formData.append('username',this.state.username)
                formData.append('email',this.state.email)
                formData.append('password',this.state.password)
                formData.append('avatar',this.state.avatar)

            this.setState({loading: true, error: false}, () => {
                ToyslandService.register(formData)
                    .then(() => {
                        this.setState({ success: true })
                    })
                    .catch(() => {
                        this.setState({ error: true, loading: false })
                    })
            })
        }

            render() {
                const errorClassName = this.state.error ? 'is-invalid' : ''

                if(this.state.success) {
                    return <Redirect to='/login' />
                }

                return (
                    <div className="Register">
                        <form onSubmit={this.state.data.name}>

                            <div className="form-group">
                                <label htmlFor="name">Nombre</label>

                                <input
                                    value={this.state.data.name}
                                    onChange={this.handleChange}
                                    autoComplete="off"
                                    name="name"
                                    type="text"
                                    className={`form-control ${errorClassName}`}
                                    id="name"
                                    placeholder="Nombre"
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="username">Username</label>

                                <input
                                    value={this.state.data.username}
                                    onChange={this.handleChange}
                                    autoComplete='off'
                                    name='username'
                                    type='text'
                                    className={`forrm-control ${errorClassName}`}
                                    id='username'
                                    placeholder='Username'                                
                                />

                            </div>

                            <div className='form-group'>
                                <label htmlFor='email'>Email</label>

                                <input 
                                    value={this.state.data.email}
                                    onChange={this.handleChange}
                                    autoComplete='off'
                                    name='email'
                                    type='email'
                                    className={`form-control ${errorClassName}`}
                                    id='email'
                                    placeholder='Email'
                                />

                            </div>

                            <div className='form-group'>
                                <label htmlFor="password">Password</label>

                                <input
                                    value={this.state.data.password}
                                    onChange={this.handleChange}
                                    autoComplete='off'
                                    name='password'
                                    type='password'
                                    className={`form-control ${errorClassName}`}
                                    placeholder='Password'
                                />

                            </div>

                            <div className='form-group'>
                                <label htmlFor='avatar'>Avatar</label>

                                <input 
                                    value={this.state.data.avatar}
                                    name='avatar'
                                    type='file'
                                    className={`form-group ${errorClassName}`}
                                    id='avatar'
                                />
                            </div>

                            <button
                                type='sunmit'
                                className='btn btn-block'
                                disabled={this.state.loading}
                            >
                                Registrarse
                            </button>

                        </form>
                    </div>
                )
            }

}

export default Register
