import React, { Component } from 'react'
import ToyslandService from '../../services/ToyslandService'
import { Link, Redirect } from 'react-router-dom'
import { WithAuthConsumer } from '../context/AuthContext'

class UpProduct extends Component {

    state = {
        data: {
            category: '',
            name: '',
            price: Number,
            coin: '',
            description: '',
            productPicture: null
        },
        error: false,
        loading: false,
        success: false,
        categories: []
    }

    handleChange = (event) => {
        const {name, value, files} = event.target

        this.setState({
            data: {
                ...this.state.data,
                [name] : files ? files[0] : value
            }
        })
    }

    handleSubmit = (event) => {
        event.preventDefault()

        const formData = new FormData()
            formData.append('profilePicture',this.state.data.product)

        this.setState({loading: true, error: false}, () => {
            ToyslandService.product(formData, this.props.product.id)
                .then((product) => {
                    this.props.setUser(product)
                    this.setState({success: true})
                })
                .catch(error => {
                    this.setState({ error: true, loading: false })
                })
            })
    }
    componentDidMount = () => {
                ToyslandService.categories().then(categories => {
            this.setState({
                categories: categories
            })
        })
    }
    render() {
        if(this.state.success) {
            return <Redirect to='/' />
        }

        console.log(this.state.categories)
        return (
            <div className="Register">
                <form onSubmit={this.handleSubmit}>


                    <div className="form-group">
                        <label htmlFor="category"></label>

                        <select id="category" name="category" form="carform">
                            {this.state.categories.map(e => {
                                return <option value={this.state.data.name}>vintage</option>

                            })}
                            <option value={this.state.data.name}>vintage</option>
                            <option value="consolas">consolas</option>
                            <option value="libros">libros</option>
                            <option value="comics">comics</option>
                            <option value="mesa">mesa</option>
                            <option value="discraces">discraces</option>
                            <option value="construccion">construccion</option>
                            <option value="puzzles">puzzles</option>
                            <option value="peluches">peluches</option>
                            <option value="aire-libre">aire libre</option>
                            <option value="teledirigido">teledirigido</option>
                            <option value="transporte">transporte</option>
                            <option value="playas">playas</option>
                            <option value="pelotas">pelotas</option>
                        </select>
                    </div>
                </form>
            </div>
        )
    }
}

export default UpProduct
