import ToyslandService from '../../services/ToyslandService'
import Lateral from '../misc/Lateral'
import { WithAuthConsumer } from '../context/AuthContext'


class Chat extends Component {

    constructor(props) {
        super(props)

        this.state = {
            chats: []
        }
    }

        componentDidMount() {
            ToyslandService.showChat(this.props.currentUser.id)
                .then(chats => {
                    this.setState({chats: chats}, () => console.log(this.state.chats))
            })
        }

        render() {            
            return(
                <div className="EditContainer">

                    <div className="LateralDiv">
                        <Lateral />
                    </div>

                     <div className='EditContainerContainer'>

                        <div className="blockOne">
                            <div className="bandeja">
                                Bandeja de Entrada
                            </div>

                            <div className="bandeja">
                                {/* {this.state.chats.products.productPicture} */}
                            </div>
                        </div>

                        <div className="blockTwo">
                            <div className="salerDate">
                                Bandeja de Entrada
                            </div>

                            <div className="SalerMessage">
                                {/* {this.state.chats.products.productPicture} */}
                            </div>

                            <div className="SendMessage">
                                {/* {this.state.chats.products.productPicture} */}
                            </div>
                        </div>

                    </div>
                </div>
            )
        }



}

export default WithAuthConsumer  (Chat)
