import axios from 'axios'

productRoutes = require('../../../back/controllers/product.controller')

const http = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:3001', // Indicas el puesrto en el que está levantada la API
  withCredentials: true
})

http.interceptors.response.use(
  response => response.data,
  error => {
    if (error.response && error.response.status === 401) {
      localStorage.clear()
      window.location.assign('/login')
    }

    return Promise.reject(error)
  }
)

const login = ({ email, password }) => http.post('/login', { email, password })
const register = (data) => http.post('/register', data)
const categories = () => http.get('/categories/:category', productRoutes.listByCategory)




export default {
  login,
  register,
  categories
}
