import React, { Component } from 'react'
import ToyslandService from '../../services/ToyslandService'
import Products from '../misc/Products'

class Search extends Component {

    state = {
        products: [],
        search: ''
    }

    searchProducts = () => {
        ToyslandService.search(this.state.search).then(search => {
            this.setState({ products: search.data})  
    })        
    }

    handleChange = (event) => {
        this.setState({
            search: event.value
        }, () => {
            this.searchProducts()
        })
    }

    render() {
        return(
  
            <div className="SearchContainer">
                <div className="SearchCont">
                    <input placeholder="Buscar..." value={this.componentDidUpdate.search} onChange={this.handleChange} />
                    {/* <button className="btn">Buscar</button> */}
                    {this.state.products.map(p => <Products {...p} key={index} />)}
                </div>
            </div>
        )
    }
} 

export default Search
