import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { WithAuthConsumer } from '../context/AuthContext'
import LogOut from '../../assets/images/menu/logout.png'

class Lateral extends Component {

    constructor(props) {
        super(props)

        const user = props.currentUser.product.find(product => product.id === props.match.params.id)

    this.state = {
        data: {
            profilePicture: this.props.currentUser.profilePicture,
        }
    }

}


    render(){
            return (


                        <div className="LateralContainer">

                            <div className="Lateral">
                                <ul>
                                    <Link to= "/profile">
                                        <li>
                                            <img src= {this.props.currentUser.profilePicture} alt="" />
                                            <p>Perfil</p>
                                    </li>
                                    </Link>
                                    <Link to= "/products">
                                    <li>
                                        <img src= '../../assets/images/menu/products-yellow.png' alt="" />
                                        <p>Productos</p>
                                    </li>
                                    </Link>
                                    <li>
                                        <img src= '../../assets/images/menu/star-yellow.png' alt="" />
                                        <p>Favoritos</p>
                                    </li>
                                    <li>
                                        <img src= '../../assets/images/menu/envelop-yellow.png' alt="" />
                                        <p>Mensajes</p>
                                    </li>
                                    <li>
                                        <img src= {LogOut} alt="" />
                                        {/* <button className="BtnLogOut" onClick={logout}>Log Out</button> */}
                                    </li>
                                </ul>
                            </div>
                        </div>           


                    )
                }


}

export default WithAuthConsumer (Lateral)
