import React, { Component } from 'react'
import ToyslandService from '../../services/ToyslandService'
import { Link, Redirect } from 'react-router-dom'
import { WithAuthConsumer } from '../context/AuthContext'

class UpProduct extends Component {

    state = {
        data: {
            category: '',
            name: '',
            price: 0,
            coin: '',
            description: '',
            productPicture: null
        },
        error: false,
        loading: false,
        success: false,
        categories: []
    }

    handleChange = (event) => {
        const {name, value, files} = event.target

        this.setState({
            data: {
                ...this.state.data,
                [name] : files ? files[0] : value
            }
        })
    }

    handleSubmit = (event) => {
        event.preventDefault()

        const formData = new FormData()
            formData.append('category',this.state.data.category)
            formData.append('name',this.state.data.name)
            formData.append('price',this.state.data.price)
            formData.append('coin',this.state.data.coin)
            formData.append('description',this.state.data.description)
            formData.append('profilePicture',this.state.data.productPicture)

        this.setState({loading: true, error: false}, () => {
            ToyslandService.product(formData, this.props.currentUser.id)
                .then((product) => {
                    this.props.setUser(product)
                    this.setState({success: true})
                })
                .catch(error => {
                    this.setState({ error: true, loading: false })
                })
            })
    }

    componentDidMount = () => {
        ToyslandService.categories().then(categories => {
            this.setState({
                categories: categories // Las de state
            })
        })
    }

    render() {
        const errorClassName = this.state.error ? 'is-invalid' : ''

        if(this.state.success) {
            return <Redirect to='/' />
        }

        console.log(this.state.categories)
        return (
            <div className="Register">
                <form onSubmit={this.handleSubmit}>

                    <div className="form-group">
                        <label htmlFor="category"></label>

                        <select id="category" name="category" form="carform">
                            {this.state.categories.map(e => {
                                return <option value={e.id}>{e.category}</option>
                            })}
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="name">Nombre</label>

                        <input 
                            value={this.state.data.name}
                            onChange={this.handleChange}
                            autoComplete="off"
                            name="name"
                            type="text"
                            className={`form-control ${errorClassName}`}
                            id="name"
                            placeholder="Nombre"                       
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="price">Precio</label>

                        <input 
                            value={this.state.data.price}
                            onChange={this.handleChange}
                            autoComplete="off"
                            name="price"
                            type="number"
                            className={`form-control ${errorClassName}`}
                            id="price"
                            placeholder="Price"                       
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="coin">Moneda</label>

                        <select id="coin" name="coin" form="coin">
                            <option value={this.state.data.coin}>€</option>
                            <option value={this.state.data.coin}>$</option>
                            <option value={this.state.data.coin}>£</option>
                        </select>

                    </div>

                    <div className="form-group">
                        <label htmlFor="description">Descripción</label>

                        <input 
                            value={this.state.data.description}
                            onChange={this.handleChange}
                            autoComplete="off"
                            name="description"
                            type="text"
                            className={`form-control ${errorClassName}`}
                            id="description"
                            placeholder="Máx 140 caracteres"                       
                        />
                    </div>


                    <div className='form-group'>
                                <label htmlFor='productPicture'>Imágenes</label>

                                <input multiple 
                                    onChange={this.handleChange}
                                    name='productPicture'
                                    type='file'
                                    className={`form-group ${errorClassName}`}
                                    id='productPicture'
                                />


                    </div>

                    <button
                        type='submit'
                        className='btn btn-block btn-primary mb-3'
                        disabled={this.state.loading}
                    >
                        Subir Producto
                    </button>

                </form>
            </div>
        )
    }
}



export default WithAuthConsumer (UpProduct)
