import React, { useState } from 'react'
import ItemsCarousel from 'react-items-carousel'


 
const Categories = ({ category, id, image, code }) => {
  const [activeItemIndex, setActiveItemIndex] = useState(0);
  const chevronWidth = 40;

  return (
    <div className="CategoriesContainer">
        <div style={{ padding: `0 ${chevronWidth}px` }}>
        <ItemsCarousel
            requestToChangeActive={setActiveItemIndex}
            activeItemIndex={activeItemIndex}
            numberOfCards={2}
            leftChevron={<button className="arrow" >{'<'}</button>}
            rightChevron={<button className="arrow" >{'>'}</button>}
            outsideChevron
            chevronWidth={chevronWidth}
        >
            <div className="categories" style={{ width: 150, height: 200, background: '#EEE' }}>
              <img src={productPicture} alt="" />
              <p>{name}</p></div>
        </ItemsCarousel>
        </div>
    </div>
  );
};

export default Categories
