import React, {Component} from 'react'
import { WithAuthConsumer } from '../context/AuthContext'
import ToyslandService from '../../services/ToyslandService'
import SingleCategoryCard from '../misc/SingleCategoryCard'



class SingleCategory extends Component {

    state= {
        products:[] // Pinto un array de productos vacío
    }

    findProducts = () => {
        ToyslandService.getAll().then(products => { // Cojo TODOS los productos
            this.setState({products: products.find(product => product.category === this.props.match.params.category)}) // Productos: coge los productos de la categoría que machean con la categoría de la url
            console.log(this.state.products)            
        })
    }

    componentDidMount() {
        this.findProducts()
    }



    render() { // Aquí hago un map cpn this.state.products

        return(
            <div className="CategoryContainer">

                        {this.props.products.map((products, index) => (
                            <SingleCategoryCard {...products} key= {index} />
                        ))}             
            </div>
        )
    }

}

export default WithAuthConsumer (SingleCategory)
