import React from 'react'

const Controls = ({ onClickDeleteProduct, onClickOrderByDate, sortedBy}) => {
    return(
        <div>
            <button
            className={"btn btn-secondary " + (sortedBy === 'name' && 'active')}
             onClick={onClickDeleteProduct}>Ordenar por Precio</button>
            <button onClick={onClickOrderByDate}>Ordenar por Fecha</button>
        </div>
    )
}

export default Controls
