import React, { Component } from 'react'
import { WithAuthConsumer } from '../context/AuthContext'
import ToyslandService from '../../services/ToyslandService'
import { Redirect } from 'react-router-dom'
import Lateral from '../misc/Lateral'

class EditProduct extends Component {

        constructor(props) {
            super(props)

            const product = this.props.currentUser.product.find(product => product.id === props.match.params.id) // encuentro el producto que tengo en el context
            this.state = {
                data: {
                    category: product.category,
                    category: product.category,
                    category: product.category,
                },
                success: false
            }
        }

        // state = {
        //     data: {
        //         category: this.props.currentUser.product.category,
        //         name: this.props.currentUser.product.name,
        //         price: this.props.currentUser.product.price,
        //         description: this.props.currentUser.product.description,
        //         productPicture: this.props.currentUser.product.productPicture               
        //     },
        //     success: false
        // }

        handleChange = (event) => {
            const {name, value, files} = event.target
            this.setState({

                data: {
                    ...this.state.data,
                    [name] : files ? files[0] : value
                }
            })
        }

        handleSubmit = (event) => {
            event.preventDefault()

            const formData = new FormData()
                formData.append('category', this.state.data.category)
                formData.append('name', this.state.data.name)
                formData.append('price', this.state.data.price)
                formData.append('description', this.state.data.descriptionory)
                formData.append('productPicture', this.state.data.productPicture)

            this.setState({loading: true, error: false}, () => {
                ToyslandService.editProduct(formData, this.props.currentUser.product.id)
                    .then((user) => {
                        this.props.setUser(user)
                        this.setState({success: true})
                    })
                    .catch(() => {
                        this.setState({error: true, loading: false})
                    })
            })
        }

        render() {    
            if(this.state.success) {

                return <Redirect to='/profile' />
            }
            return (
                <div className="EditContainer">

                    <div className="LateralDiv">
                        <Lateral />
                    </div>

                    <div className='EditContainerContainer'>

<div className="TuPerfilContainer">
    <p className="tit">Tu perfil</p>
    <p className="subtit">Aquí podrás ver y editar los datos de tu perfil</p>
</div>


                            {/* IMAGE */}


<div className="Edit-Image">
    <form onSubmit={this.handleSubmit} encType="multipart/form-data">



        <div className="form-container">

            <div className="form-title">Imagen de perfil</div>

                <div className='form-group'>
                    <label htmlFor="name">Imagen Principal</label>

                <div className="ProfileImageContainer">
                    <img src={this.props.currentUser.product.productPicture} alt="my prfile pic" />
                </div>

                    <input 
                        onChange={this.handleChange}
                        name='avatar'
                        type='file'
                        className='form-group'
                        id='avatar'
                    />
                </div>

                <button
                    type='submit'
                    className='btn btn-toysland'
                    disabled={this.state.loading}
                >
                    Guardar Cambios
                </button>
        </div>


    </form>
</div>                        

<div className="Edit">
    <form onSubmit={this.handleSubmit}>

    <div className="form-container">     

        <div className="form-title">Mi Info</div>

                <div className="form-group-info">
                    <label htmlFor="name">Nombre</label>

                    <input
                        value={this.state.data.name}
                        onChange={this.handleChange}
                        autoComplete="off"
                        name="name"
                        type="text"
                        className='form-control'
                        id="name"
                        placeholder="Nombre"
                    />
                </div>

                <div className="form-group-info">
                    <label htmlFor="username">Username</label>

                    <input
                        value={this.state.data.username}
                        onChange={this.handleChange}
                        autoComplete='off'
                        name='username'
                        type='text'
                        className='form-control'
                        id='username'
                        placeholder='Username'                                
                    />

                </div>

                <div className='form-group-info'>
                    <label htmlFor='email'>Email</label>

                    <input 
                        value={this.state.data.email}
                        onChange={this.handleChange}
                        autoComplete='off'
                        name='email'
                        type='email'
                        className='form-control'
                        id='email'
                        placeholder='Email'
                    />

                </div>

                <div className='form-group-info'>
                    <label htmlFor='cp'>cp</label>

                    <input 
                        value={this.state.data.cp}
                        onChange={this.handleChange}
                        autoComplete='off'
                        name='cp'
                        type='cp'
                        className='form-control'
                        id='cp'
                        placeholder='cp'
                    />

                </div>

                <div className='form-group-info'>
                    <label htmlFor='city'>city</label>

                    <input 
                        value={this.state.data.city}
                        onChange={this.handleChange}
                        autoComplete='off'
                        name='city'
                        type='city'
                        className='form-control'
                        id='city'
                        placeholder='city'
                    />

                </div>

                <div className='form-group-info'>
                    <label htmlFor='country'>country</label>

                    <input 
                        value={this.state.data.country}
                        onChange={this.handleChange}
                        autoComplete='off'
                        name='country'
                        type='country'
                        className='form-control'
                        id='country'
                        placeholder='country'
                    />

                </div>

                    <button
                        type='submit'
                        className='btn btn-toysland'
                        disabled={this.state.loading}
                    >
                        Guardar Cambios
                    </button>


                 </div>{/* //Fin de form-container */}



                    </form>
                </div>
            </div>
        <div className="Space"></div>
    </div>

            )
        }
}

export default WithAuthConsumer(EditProduct)
