import React, { Component } from 'react'
import ToyslandService from '../../services/ToyslandService'
import { Redirect } from 'react-router-dom'
import { WithAuthConsumer } from '../context/AuthContext'
import Lateral from '../misc/Lateral'
import ProductCard from './ProductCard'

class Products extends Component {


    state = {
        products: this.props.currentUser.product
    }

    render() {
        console.log(this.props.currentUser)
        return(
            <div className="EditContainer">


                <div className="LateralDiv">
                    <Lateral />
                </div>

                    <p className="tit">Tus Productos</p>
                    <p className="subtit">Aquí podrás ver y editar tus productos</p>

                <div className="ProfileImageContainer">
                    <div>{this.props.currentUser.product.name}</div>
                </div>
                <div className="MyProducts">
                    <div clasName="row">
                        {this.state.products.map((product, index) => (
                            <ProductCard {...product} key= {index} />
                        ))}
                    </div>

                </div>
            </div>
        )
    }

}

export default WithAuthConsumer (Products)
